export interface Observable<T> {
  subscribe(observer: Observer<T>): Subscription;
  subscribe(next?: (value: T) => void, error?: (err: any) => void, complete?: () => void): Subscription;
  pipe<R>(...operations: OperatorFunction<any, any>[]): Observable<R>;
  forEach(next: (value: T) => void): Promise<void>;
  toPromise(): Promise<T | undefined>;
}

export interface Observer<T> {
  next: (value: T) => void;
  error: (err: any) => void;
  complete: () => void;
}

export interface Subscription {
  unsubscribe(): void;
  readonly closed: boolean;
}

export interface OperatorFunction<T, R> {
  (source: Observable<T>): Observable<R>;
}

export declare function of<T>(...values: T[]): Observable<T>;
export declare function from<T>(input: Iterable<T> | Promise<T>): Observable<T>;
export declare function map<T, R>(project: (value: T) => R): OperatorFunction<T, R>;
export declare function filter<T>(predicate: (value: T) => boolean): OperatorFunction<T, T>;
